document.addEventListener("DOMContentLoaded",(function(){document.getElementById("privacyPolicyBtn").addEventListener("click",(function(e){e.preventDefault(),document.getElementById("privacyPolicyModal").style.display="block"})),document.getElementById("termsOfServiceBtn").addEventListener("click",(function(e){e.preventDefault(),document.getElementById("termsOfServiceModal").style.display="block"})),document.getElementById("communityGuidelinesBtn").addEventListener("click",(function(e){e.preventDefault(),document.getElementById("communityGuidelinesModal").style.display="block"})),document.getElementById("disclaimerBtn").addEventListener("click",(function(e){e.preventDefault(),document.getElementById("disclaimerModal").style.display="block"})),document.querySelectorAll("[data-modal]").forEach((e=>{e.addEventListener("click",(function(){const e=this.getAttribute("data-modal");document.getElementById(e).style.display="none"}))}))}));